import React, { useState } from "react";
import styles from "./Navbar.module.css";

const NavItem = ({ handler, txt, targetpage }) => {
  return (
    <li>
      <button onClick={handler} targetpage={targetpage}>
        {txt}
      </button>
    </li>
  );
};

export default function Navbar({ setPage }) {
  const [isActive, setIsActive] = useState(false);

  const onClick_MenuBtn_Handler = () => setIsActive(!isActive);

  const onClick_link_Handler = (event) => {
    setPage(event.target.attributes.targetpage.value);
    setIsActive(false);
    window.scrollTo(0, 0);
  };

  const onClick_Landing_Handler = (event) => {
    setPage(event.target.attributes.targetpage.value);
    setIsActive(false);
    window.scrollTo(0, 0);
  };
  const onClick_UberUns_Handler = () => {
    setPage("UeberUns");
    setIsActive(false);
    window.scrollTo(0, 0);
  };
  const onClick_Aktuelles_Handler = () => {
    setPage("Aktuelles");
    setIsActive(false);
    window.scrollTo(0, 0);
  };
  const onClick_Sportliches_Handler = () => {
    setPage("Sportliches");
    setIsActive(false);
    window.scrollTo(0, 0);
  };
  const onClick_Impressum_Handler = () => {
    setPage("Impressum");
    setIsActive(false);
    window.scrollTo(0, 0);
  };

  return (
    <nav className={`${styles.nav} ${isActive ? styles.nav__open : ""}`}>
      <button className={styles.nav__menuBtn} onClick={onClick_MenuBtn_Handler}>
        <i className="fa-regular fa-circle-right"></i>
      </button>
      <button
        className={styles.nav__logo}
        onClick={onClick_link_Handler}
        targetpage="Landing"
      >
        SG Eintracht Halfing E.V.
      </button>
      <ul className={styles.nav__ul}>
        <NavItem
          handler={onClick_link_Handler}
          targetpage="UeberUns"
          txt="Ãœber Uns"
        />
        <NavItem
          handler={onClick_link_Handler}
          targetpage="Aktuelles"
          txt="Aktuelles"
        />
        <NavItem
          handler={onClick_link_Handler}
          targetpage="Sportliches"
          txt="Sportliches"
        />
        <NavItem
          handler={onClick_link_Handler}
          targetpage="Impressum"
          txt="Impressum"
        />
      </ul>
    </nav>
  );
}
