import React, { useState } from "react";
import styles from "./Navbar.module.css";

const NavItem = ({ handler, txt }) => {
  return (
    <li>
      <button onClick={handler}>{txt}</button>
    </li>
  );
};

export default function Navbar({ setPage }) {
  const [isActive, setIsActive] = useState(false);

  const onClick_MenuBtn_Handler = () => setIsActive(!isActive);
  const onClick_Landing_Handler = (event) => {
    console.log(event.target.attributes.targetpage.name);
    setPage("");
    setIsActive(false);
    window.scrollTo(0, 0);
  };
  const onClick_UberUns_Handler = () => {
    setPage("UeberUns");
    setIsActive(false);
    window.scrollTo(0, 0);
  };
  const onClick_Aktuelles_Handler = () => {
    setPage("Aktuelles");
    setIsActive(false);
    window.scrollTo(0, 0);
  };
  const onClick_Sportliches_Handler = () => {
    setPage("Sportliches");
    setIsActive(false);
    window.scrollTo(0, 0);
  };
  const onClick_Impressum_Handler = () => {
    setPage("Impressum");
    setIsActive(false);
    window.scrollTo(0, 0);
  };

  return (
    <nav className={`${styles.nav} ${isActive ? styles.nav__open : ""}`}>
      <button className={styles.nav__menuBtn} onClick={onClick_MenuBtn_Handler}>
        <i className="fa-regular fa-circle-right"></i>
      </button>
      <button
        className={styles.nav__logo}
        onClick={onClick_Landing_Handler}
        targetpage="Landing"
      >
        SG Eintracht Halfing E.V.
      </button>
      <ul className={styles.nav__ul}>
        <NavItem handler={onClick_UberUns_Handler} txt="Ãœber Uns" />
        <NavItem handler={onClick_Aktuelles_Handler} txt="Aktuelles" />
        <NavItem handler={onClick_Sportliches_Handler} txt="Sportliches" />
        <NavItem handler={onClick_Impressum_Handler} txt="Impressum" />
      </ul>
    </nav>
  );
}
